import "./TypingArea.css";

export default function TypingArea() {
    let chars = "asdf";

    function randomIntFromInterval(min, max) {
        // min and max included
        return Math.floor(Math.random() * (max - min + 1) + min);
    }

    function createText(chars, numWords) {
        let charLen = chars.length;
        let res;
        let resList = [];
        for (let i = 0; i < numWords; i++) {
            res = "";
            for (let j = 0; j < randomIntFromInterval(3, 5); j++) {
                res += chars.charAt(Math.floor(Math.random() * charLen));
            }
            resList.append(res);
        }
        console.log("opp");
        return resList.join(" ");
    }
    console.log("here");
    let text = createText(chars, 40);
    console.log(text);

    return <div className="typing">{text}</div>;
}
