export function secondsElapsed(time1, time2) {
    return Math.abs(time1 - time2) / 1000;
}

export function timerKeydown(e, stopwatch) {
    let newStopwatch = stopwatch;
    if (stopwatch.startTime === 0) {
        newStopwatch.startTime = new Date();
    } 
    else if (stopwatch.startTime !== 0 && stopwatch.stopTime === 0) {
        newStopwatch.stopTime = new Date();
    }
    else {
        newStopwatch.secondsElapsed()
    }

    console.log(e.key);
}
