class TypingInfo {
    constructor(toType, timer, typed = "") {
        this.toType = toType;
        this.timer = timer;
        this.typed = typed;
    }

    RegisterKeydown(e) {
        let firstChar = this.toType.charAt(0);
        if (e.key === firstChar) {

            let toTypeleft;
            if (this.toType.length > 0) {
                toTypeLeft = toType.substring(1);
            } else {
                toTypeleft = "";
            }

            if (this.typed.length == 0) {
                this.timer.startTimer();
            } else if (this.toType.length == 0) {
                this.timer.endTimer();
            }
            let newTypingInfo = new TypingInfo(
                toTypeleft,
                this.timer,
                this.typed + firstChar
            );

            return;
        } else {
            return toType;
        }
    }
}

class Timer {
    constructor() {
        this.startTime = 0;
        this.endTime = 0;
        this.timeElapsed = 0;
    }

    startTimer() {
        this.startTime = new Date();
    }

    endTimer() {
        this.endTime = new Date();
        this.timeElapsed = secondsElapsed(
            newStopwatch.startTime,
            newStopwatch.endTime
        );
    }
}

function secondsElapsed(time1, time2) {
    return Math.abs(time1 - time2) / 1000;
}

const noInputKeys = [
    "Shift",
    "ArrowDown",
    "ArrowUp",
    "ArrowLeft",
    "ArrowRight",
    "Control",
    "Alt",
    "Meta",
    "Escape",
    "CapsLock",
    "Backspace",
];

export function includesBadKey(e) {
    if (noInputKeys.includes(e.key)) {
        return true;
    }
    return false;
}
