import "./Options.css";

export default function Options({ lessonState }) {
    let [lessonNum, setLessonNum] = lessonState;

    function generateLesson(lessonNum) {
        console.log(lessonNum);
    }

    function buildLessonList(numOfLessons, lessonNum) {
        let lessonList = [...Array(numOfLessons).keys()];
        return lessonList.map((val) => {
            let lessonNum = val + 1;
            let classes = (lessonNum == val) ? "lesson-item" : "lesson-item"
            return (
                <div
                    id={"lesson" + String(lessonNum)}
                    className="lesson-item"
                    key={lessonNum}
                    onClick={() => generateLesson(lessonNum)}
                >
                    Lesson {lessonNum}
                </div>
            );
        });
    }

    return (
        <div className="option-container">
            <div>SELECT A LESSON</div>
            {buildLessonList(14)}
        </div>
    );
}
