// component
import TypingArea from "./components/TypingArea";
import Options from "./components/Options";

import { useState } from "react";
import { removeActive, addActive } from "./assets/classManipulation";

import "./App.css";

function App() {
    let [lessonNum, setLessonNum] = useState(1);
    document.getElementsByClassName("active-lesson");
    document.getElementById("lesson" + lessonNum).classList += "active-lesson";

    return (
        <div className="App">
            <Options lessonNum={lessonNum} />
            <TypingArea lessonNum={lessonNum} />
        </div>
    );
}

export default App;
